        public override Task<HttpResponse> HandleRequest(HttpRequest request, HttpResponse response, IFcContext fcContext)
        {
            if (reflection == null)
            {
                Reflection.PATH = "/home/UMC/";

                String path = Environment.GetEnvironmentVariable("PATH");
                if (String.IsNullOrEmpty(path) == false)
                {
                    Reflection.PATH = String.Format("/home/UMC/{0}/", path.Trim('/'));

                }
                reflection = new Reflection(fcContext.Logger);

            }
            reflection._logger = fcContext.Logger;
            return base.HandleRequest(request, response, fcContext);
        }"},"format":{"text":"{code}"},"style":{},"_CellName":"CMSCode"},{"value":{"m0":"发布后打包zip包，上传阿里云函数计算中新建http触发器函数，在函数入口：UMC.FC::UMC.FC.WebServlet::HandleRequest，运行环境中选择dotnetcore2.1，配置如下图："},"format":{"text":"{m0}"},"style":{},"_CellName":"CMSText"},{"value":{"src":"http://www.365lu.cn/UserResources/1obs7h6/1603530544813/image.png"},"format":{},"style":{"padding":"0 10 0 10"},"_CellName":"CMSImage"},{"value":{"m0":"由于阿里云serverless函数计算是无存储空间，需要在函数配置开通挂载NAS文件系统，并在本地目录配置成“","m1":"/home/UMC","m2":"”，配置如下图："},"format":{"text":"{m0}{m1}{m2}"},"style":{"m1":{"color":"#cc6600"}},"_CellName":"CMSText"},{"value":{"src":"http://www.365lu.cn/UserResources/1obs7h6/1603530993826/image.png"},"format":{},"style":{"padding":"0 10 0 10"},"_CellName":"CMSImage"},{"value":{"m0":"到处阿里云serverless函数计算部署就算完成了，其他配置受阿里云无服务函数计算限制，请参考","m1":"阿里云函数计算说明"},"format":{"text":"{m0}{m1}"},"style":{"m1":{"click":{"key":"Url","send":"https://help.aliyun.com/product/50980.html"}}},"_CellName":"CMSText"},{"value":{"m0":"第一次配置阿里云无服务函数计算需要学习新知识，但阿里云无服务函数计算提供的免费额度，能让个人的应用免费运行起来。"},"format":{"text":"{m0}"},"style":{},"_CellName":"CMSText"}]
