    {
        public Startup(IConfiguration configuration)
        {
            Configuration = configuration;
        }
        public IConfiguration Configuration { get; }
        public void ConfigureServices(IServiceCollection services)
        {
            UMC.Data.Sql.OracleDbProvider.Instance = Oracle.ManagedDataAccess.Client.OracleClientFactory.Instance;
            UMC.Data.Sql.SQLiteDbProvider.Instance = SqliteFactory.Instance;
            UMC.Data.Sql.SqlDbProvider.Instance = System.Data.SqlClient.SqlClientFactory.Instance;

        }
        internal Task Process(HttpContext context)
        {
            return Task.Run(() =>
            {
                new Web.WebServlet().ProcessRequest(new NetContext(context.Request, context.Response, context.Connection.RemoteIpAddress.ToString()));
            });
        }
        public void Configure(IApplicationBuilder app, IHostingEnvironment env)
        {
            if (env.IsDevelopment())
            {
                app.UseDeveloperExceptionPage();
            }
            app.Run(Process);
        }
    }"},"format":{"text":"{code}"},"style":{},"_CellName":"CMSCode"},{"value":{"m0":"如果各没有问题，就可以生成，或者调试了"},"format":{"text":"{m0}"},"style":{},"_CellName":"CMSText"},{"value":{"Key":3,"text":"阿里云函数计算宿主"},"format":{"text":"{text}"},"style":{"font-weight":"bold","font-size":"22"},"_CellName":"CMSText"},{"value":{"m0":"此实例通过","m1":"阿里云函数计算C# HTTP函数","m2":"实现无服务器架构，让基于UMC的应用只要关心业务实现，不再关心服务端运维。"},"format":{"text":"{m0}{m1}{m2}"},"style":{"m1":{"click":{"key":"Url","send":"https://help.aliyun.com/document_detail/159228.html"}}},"_CellName":"CMSText"},{"value":{"m0":"在Asp.net Core实例引用函数计算使用C#编写HTTP函数，需要Nuget引入Aliyun.Serverless.Core和Aliyun.Serverless.Core.Http包。UMC.FC.WebServlet继承Aliyun.Serverless.Core.Http.FcHttpEntrypoint，并实现，代码如下："},"format":{"text":"{m0}"},"style":{},"_CellName":"CMSText"},{"value":{"type":"","code":"  protected override void Init(IWebHostBuilder builder)
        {
            builder.UseStartup<Startup>();
        }
        public override Task<HttpResponse> HandleRequest(HttpRequest request, HttpResponse response, IFcContext fcContext)
        {
            if (reflection == null)
            {
                Reflection.PATH = "/home/UMC/";

                String path = Environment.GetEnvironmentVariable("PATH");
                if (String.IsNullOrEmpty(path) == false)
                {
                    Reflection.PATH = String.Format("/home/UMC/{0}/", path.Trim('/'));

                }
                reflection = new Reflection(fcContext.Logger);

            }
            reflection._logger = fcContext.Logger;
            return base.HandleRequest(request, response, fcContext);
