[{"value":{"m0":"在UMC Web页面开发中，我们通过history.popstate事件来配对用$.page注册页面层和$.tpl注册的模板页面层,在用$.page注册或$.tpl注册都需要传入初始化方法，在激活页面会把注册页面层的关联的root和title和menu来回调初始化方法，来完成注册页面层的初始化，"},"format":{"text":"{m0}"},"style":{},"_CellName":"CMSText"},{"value":{"flex":6,"grid":[[{"style":{"text-align":"left","flex":2},"data":{"m0":"事件名"},"format":"{m0}"},{"style":{"text-align":"left","flex":2},"data":{"m0":"参数类型"},"format":"{m0}"},{"style":{"text-align":"left","flex":2},"data":{"m0":"说明"},"format":"{m0}"}],[{"style":{"text-align":"left","flex":2},"data":{"m0":"hash"},"format":"{m0}"},{"style":{"text-align":"left","flex":2},"data":{"m0":"字典"},"format":"{m0}"},{"style":{"text-align":"left","flex":2},"data":{"m0":"变化事件hash把Query参数字典化，在模板页中会把后面路径用key来参入"},"format":"{m0}"}],[{"style":{"text-align":"left","flex":2},"data":{"m0":"active"},"format":"{m0}"},{"style":{"text-align":"left","flex":2},"data":{"m0":"元"},"format":"{m0}"},{"style":{"text-align":"left","flex":2},"data":{"m0":"当root再次显示触发"},"format":"{m0}"}],[{"style":{"text-align":"left","flex":2},"data":{"m0":"backstage"},"format":"{m0}"},{"style":{"text-align":"left","flex":2},"data":{"m0":"无"},"format":"{m0}"},{"style":{"text-align":"left","flex":2},"data":{"m0":"进入缓存触发事件，当返回false时，则当前root不进入缓存，还在主DOM中，只是隐藏"},"format":"{m0}"}]]},"format":{},"style":{},"_CellName":"CMSGrid"},{"value":{"Key":3,"text":"页面层注册方法"},"format":{"text":"{text}"},"style":{"font-weight":"bold","font-size":"22"},"_CellName":"CMSText"},{"value":{"type":"","code":"$.page('main', '我的门店', false, function (root, title, menu) {
        //title是标题，默认当前注册的标题，menu为菜单区域，可以追加菜单内容
        root.on('hash', function (e, v) {
                //v参数是把Query字典化参数，在模板页中v.key是配对后路径后面的参数
            })
            .on('event', function (e,v,d) {
                //在root用只要点击a[ui-event]标识的元素，就会触发此事件v为ui-event属性值，d为dom
            })
            .on('active', function () { })
            .on('backstage', function () { })
            .ui('...', function (e, d) {
